INSERT INTO `lol_spells` (`id`, `spell_id`, `name`, `description`, `summoner_level`, `keyname`, `cooldown`, `gamemodes`, `lang`) VALUES
(1, 1, 'Limpiar', 'Elimina todas las debilitaciones y efectos de hechizos de invocador que afectan al campeón y reduce en un 65% la duración de las debilitaciones recibidas durante 3 segundos.', 6, 'SummonerBoost', 210, 'CLASSIC;ODIN;TUTORIAL;ARAM;ASCENSION', 'es_ES'),
(2, 12, 'Teleportar', 'Después de 3,5 segundos de preparación, teleporta a tu campeón al súbdito, estructura o guardián aliados que hayas escogido.', 6, 'SummonerTeleport', 300, 'CLASSIC;TUTORIAL', 'es_ES'),
(3, 30, '¡Al Rey!', 'Te envía rápidamente al lado del Rey Poro.', 1, 'SummonerPoroRecall', 10, 'KINGPORO', 'es_ES'),
(4, 14, 'Prender', 'Hace arder a un campeón enemigo durante 5 segundos, infligiéndole 70-410 (según el nivel del campeón) de daño verdadero, garantiza la visión del objetivo y reduce los efectos de curación que éste reciba.', 10, 'SummonerDot', 180, 'CLASSIC;ODIN;TUTORIAL;ARAM;ASCENSION', 'es_ES'),
(5, 6, 'Fantasmal', 'Tu campeón puede moverse entre unidades y tiene un 27% más de velocidad de movimiento durante 10 segundos.', 1, 'SummonerHaste', 210, 'CLASSIC;ODIN;TUTORIAL;ARAM;ASCENSION', 'es_ES'),
(6, 32, 'Marca', 'Lanza una bola de nieve en línea recta hacia tus enemigos. Si alcanzas a uno queda marcado y a continuación tu campeón puede desplazarse con rapidez hasta él.', 1, 'SummonerSnowball', 40, 'ARAM', 'es_ES'),
(7, 7, 'Curar', 'Restaura 90-345 de vida (según el nivel del campeón) y otorga un 30% de velocidad de movimiento adicional durante 1 segundo tanto a ti como al campeón aliado seleccionado. El efecto de curación se reduce a la mitad en el caso de unidades beneficiadas recientemente por este hechizo de Invocador.', 1, 'SummonerHeal', 240, 'CLASSIC;ODIN;TUTORIAL;ARAM;ASCENSION', 'es_ES'),
(8, 11, 'Aplastar', 'Causa entre 390 y 1000 de daño verdadero (dependiendo del nivel del campeón) a monstruos épicos o gigantes, o súbditos enemigos.', 10, 'SummonerSmite', 90, 'CLASSIC;TUTORIAL', 'es_ES'),
(9, 3, 'Extenuación', 'Extenúa al campeón enemigo objetivo, reduciéndole un 30% de su velocidad de movimiento y velocidad de ataque, la armadura y la resistencia mágica por 10 y un 40% de su capacidad total de daño durante 2,5 segundos.', 4, 'SummonerExhaust', 210, 'CLASSIC;ODIN;TUTORIAL;ARAM;ASCENSION', 'es_ES'),
(10, 31, 'Porolanzamiento', 'Lanza un poro a los enemigos. Si aciertas, a continuación puedes desplazarte rápidamente al objetivo.', 1, 'SummonerPoroThrow', 20, 'KINGPORO', 'es_ES'),
(11, 13, 'Claridad', 'Restaura el 40% del maná máximo a tu campeón. También restaura a los aliados el 40% de su maná máximo.', 1, 'SummonerMana', 180, 'ODIN;TUTORIAL;ARAM;ASCENSION', 'es_ES'),
(12, 21, 'Barrera', 'Protege a tu campeón, absorbiendo 115-455 de daño (según el nivel del campeón) durante 2 segundos.', 4, 'SummonerBarrier', 210, 'ARAM;CLASSIC;TUTORIAL;ODIN;ASCENSION', 'es_ES'),
(13, 2, 'Clarividente', 'Muestra a tu equipo una pequeña zona del mapa durante 5 segundos.', 8, 'SummonerClairvoyance', 55, 'ODIN;TUTORIAL;ASCENSION', 'es_ES'),
(14, 4, 'Destello', 'Teleporta a tu campeón una corta distancia hacia la posición del cursor.', 8, 'SummonerFlash', 300, 'CLASSIC;ODIN;TUTORIAL;ARAM;ASCENSION', 'es_ES'),
(15, 17, 'Guarnición', 'Torreta aliada: concede una gran Regeneración durante 8 segundos. Torreta enemiga: reduce el daño que inflige en un 80% durante 8 segundos.', 1, 'SummonerOdinGarrison', 210, 'ODIN', 'es_ES');
