<?phprequire('../core.php');$query = $db->query('SELECT champ_keyname,champname,title FROM lol_champs');$json = array();$json_num = 0;while($row = $query->fetch_array()){	$json[$json_num] = array();	$json[$json_num]['url'] = URL.'/champions/'.$row['champ_keyname'];	$json[$json_num]['name'] = $row['champname'];	$json[$json_num]['description'] = $row['title'];	$json[$json_num]['img'] = URL.'/game/champions/base/'.$row['champname'].'.png';		$json_num++;}echo json_encode($json);